mapper.select:
select hour
from FLICKR_EUROPE_COUNT
where ID = #{id} and RADIUS = ${radius}

select day
from FLICKR_EUROPE_COUNT
where ID = #{id} and RADIUS = ${radius}

select month
from FLICKR_EUROPE_COUNT
where ID = #{id} and RADIUS = ${radius}

select year
from FLICKR_EUROPE_COUNT
where ID = #{id} and RADIUS = ${radius}

select total
from FLICKR_EUROPE_COUNT
where ID = #{areaid} and RADIUS = ${radius}
